mapper.select:
select
inst.*,ins.instrumentName,ins.instrumentNo,ins.instrumentType,ins.classNo,
(select speciesName from clims_species where id = ins.classNo) as
className,
ins.price,ins.manufacturer,ins.productionDate,ins.country,ins.number,ins.note,
(select locName from clims_location where id = inst.locNo) as locName
from clims_instStock inst,clims_instrument ins
where inst.instrumentId=ins.id

	and ins.instrumentName like CONCAT('%',#{instrumentName},'%')


	and ins.instrumentNo like CONCAT('%',#{instrumentNo},'%')


	and ins.classNo = #{classNo}


	and ins.price >= #{lowPrice}  


	and ins.price <= #{highPrice}


	and ins.productionDate >= #{startDate}  


	and ins.productionDate <= #{endDate}


	and inst.stockNumber > #{stockNumber}

order by inst.creationDate DESC limit #{startPageNo},#{pageSize}

select count(1) from clims_instStock inst,clims_instrument ins
where inst.instrumentId=ins.id

	and ins.instrumentName like CONCAT('%',#{instrumentName},'%')


	and ins.instrumentNo like CONCAT('%',#{instrumentNo},'%')


	and ins.classNo = #{classNo}


	and ins.price >= #{lowPrice}  


	and ins.price <= #{highPrice}


	and ins.productionDate >= #{startDate}  


	and ins.productionDate <= #{endDate}


	and inst.stockNumber > #{stockNumber}

mapper.update:
update clims_instStock
dept = #{dept}, stockManager = #{stockManager}, managerTel = #{managerTel}, locNo = #{locNo}, stockNumber = #{stockNumber},
where instrumentId = #{instrumentId}

mapper.select:
select inst.*,ins.instrumentName,ins.instrumentNo,ins.instrumentType,ins.classNo, (select valueName from clims_dataDictionary d where d.typeCode='INSTRUMENT_STATUS' and d.valueId=inst.status) as statusName, (select speciesName from clims_species where id = ins.classNo) as className, ins.price,ins.manufacturer,ins.productionDate,ins.country, ins.warranty,ins.maintainable,ins.purchaseDate,ins.number,ins.note, (select locName from clims_location where id = inst.locNo) as locName, (select userName from clims_user where id = inst.assignManagerId) as assignManager, (select userName from clims_user where id = inst.createdBy) as createdByName from clims_instAssign PARTITION (p1) inst,clims_instrument ins where inst.instrumentId=ins.id
and ins.instrumentNo like CONCAT('%',#{instrumentNo},'%')
and inst.dept = #{dept}
and inst.status = #{status}
and inst.opacifyToAdmin = #{opacifyToAdmin}
and ins.maintainable = #{maintainable}
order by inst.status ASC,inst.creationDate DESC
limit #{startPageNo},#{pageSize}

select inst.*,ins.instrumentName,ins.instrumentNo,ins.instrumentType,ins.classNo, (select valueName from clims_dataDictionary d where d.typeCode='INSTRUMENT_STATUS' and d.valueId=inst.status) as statusName, (select speciesName from clims_species where id = ins.classNo) as className, ins.price,ins.manufacturer,ins.productionDate,ins.country, ins.warranty,ins.maintainable,ins.purchaseDate,ins.number,ins.note, (select locName from clims_location where id = inst.locNo) as locName, (select userName from clims_user where id = inst.assignManagerId) as assignManager, (select userName from clims_user where id = inst.createdBy) as createdByName from clims_instAssign PARTITION (p2) inst,clims_instrument ins where inst.instrumentId=ins.id
and ins.instrumentNo like CONCAT('%',#{instrumentNo},'%')
and inst.dept = #{dept}
and inst.status = #{status}
and inst.opacifyToAdmin = #{opacifyToAdmin}
and ins.maintainable = #{maintainable}
order by inst.status ASC,inst.creationDate DESC
limit #{startPageNo},#{pageSize}

 
		select count(1) from clims_instAssign partition(p1) inst,clims_instrument ins where inst.instrumentId=ins.id
		
			and ins.instrumentNo like CONCAT('%',#{instrumentNo},'%')
		
		
			and inst.dept = #{dept}
		
		
			and inst.status = #{status}
		
		
			and inst.opacifyToAdmin = #{opacifyToAdmin}
		
		
			and ins.maintainable = #{maintainable}

 
		select count(1) from clims_instAssign partition(p2) inst,clims_instrument ins
		where inst.instrumentId=ins.id
		
			and ins.instrumentNo like CONCAT('%',#{instrumentNo},'%')
		
		
			and inst.dept = #{dept}
		
		
			and inst.status = #{status}
		
		
			and inst.opacifyToAdmin = #{opacifyToAdmin}
		
		
			and ins.maintainable = #{maintainable}

select xin.*,m.id as m_id,m.repairmanName,m.phone,m.sex,m.profession 
from (select inst.*,ins.instrumentName,ins.instrumentNo, ins.instrumentType,ins.classNo, 
			 ins.price,ins.manufacturer,ins.productionDate,
			 ins.country, ins.warranty,ins.number,ins.maintainable,ins.purchaseDate,ins.note,
			 (select speciesName from clims_species where id = ins.classNo) as className,
			 (select userName from clims_user where id = inst.createdBy) as createdByName,
			 (select valueName from clims_dataDictionary d 
			  where d.typeCode='INSTRUMENT_STATUS' and d.valueId=inst.status) as statusName
	  from clims_instRepair inst,clims_instrument ins
	  where inst.instrumentId = ins.id
	  and inst.dept = #{dept}
	  and inst.status = #{status}
	  and inst.createdBy = #{createdBy}
	  order by inst.status ASC,inst.creationDate DESC
	  
			limit #{startPageNo},#{pageSize}
	  
) xin,clims_repairman m
where xin.classNo = m.profession

select count(1) from clims_instRepair inst,clims_instrument ins
where inst.instrumentId=ins.id
and inst.dept = #{dept}
and inst.status = #{status}
and inst.createdBy = #{createdBy}

mapper.update:
update clims_instRepair
repairmanId = #{repairmanId}, status = #{status}, checkDate = #{checkDate}, repairDetail = #{repairDetail}, bookDate = #{bookDate}, address = #{address}, applyPerson = #{applyPerson}, applyPhone = #{applyPhone},
where id = #{id}

mapper.select:
select inst.*,ins.instrumentName,ins.instrumentNo,ins.instrumentType,ins.classNo,
	(select speciesName from clims_species where id = ins.classNo) as className,
	ins.price,ins.manufacturer,ins.productionDate,ins.country,ins.number,ins.note,
	(select userName from clims_user where id = inst.createdBy) as createdByName,
	(select valueName from clims_dataDictionary d where d.typeCode='INSTRUMENT_STATUS' and d.valueId=inst.status) as statusName
from clims_instScrap inst,clims_instrument ins where inst.instrumentId=ins.id

	and inst.dept = #{dept}


	and inst.status = #{status}

order by inst.creationDate ASC

	limit #{startPageNo},#{pageSize}

select count(1) from clims_instScrap inst,clims_instrument ins
where inst.instrumentId=ins.id

	and inst.dept = #{dept}


	and inst.status = #{status}

mapper.update:
update clims_instScrap
dealer = #{dealer}, dealerTel = #{dealerTel}, dealDate = #{dealDate}, status = #{status},
